// Topic Parsing
var parts = msg.topic.split("/");

var sector = parts[0];
var line = parts[1];
var engine = parts[2];
var sensorType = parts[3];

// Payload Extraction
var value = msg.payload.value;
var unit = msg.payload.unit;
var timestamp = new Date(msg.payload.timestamp); 

// Threshold Extraxtion
var limits = msg.payload.metadata || {}; 
var warnLimit = limits.warning_threshold;
var critLimit = limits.critical_threshold;

// Alert Logic
var status = "OK";
var alertPriority = "NONE";
var alertMessage = "";

if (critLimit !== undefined && warnLimit !== undefined) {
    if (value >= critLimit) {
        status = "CRITICAL";
        alertPriority = "HIGH";
        alertMessage = `CRITICAL ALERT: ${engine} ${sensorType} reached ${value} ${unit} (Limit: ${critLimit})`;
    } else if (value >= warnLimit) {
        status = "WARNING";
        alertPriority = "LOW";
        alertMessage = `WARNING: ${engine} ${sensorType} is high: ${value} ${unit} (Limit: ${warnLimit})`;
    }
}

// OUTPUT 1: INFLUXDB
var dbMessage = {
    payload: 
    [
        {
            value: value,
            unit: unit,
            warning_limit: warnLimit || 0,
            critical_limit: critLimit || 0
        },
        {
            sector: sector,
            line: line,
            engine: engine,
            sensor_type: sensorType,
        },
    ]    
};

// OUTPUT 2: ALERTING
var alertMessageObj = null;

if (status !== "OK") {
    alertMessageObj = {
        payload: {
            topic: msg.topic,
            engine: engine,
            sensor: sensorType,
            value: value,
            status: status,
            priority: alertPriority,
            message: alertMessage,
            timestamp: timestamp
        }
    };
}


return [dbMessage, alertMessageObj];

