// Configuration
const TARGET_ASSET_ID = "sector_1_line_1_engine_1";
const SEQUENCE_LENGTH = 50;
const AI_SERVICE_URL = "http://ai-service:8000/predict-rul";

// Physics Constants
const FREQ_HZ = 50;
const G_TO_MMS2 = 9806.65;
const OMEGA = 2 * Math.PI * FREQ_HZ;

// Memory 
// Use 'flow' context to persist data between messages
var buffer = flow.get('target_engine_buffer') || {
    currentReading: {},
    history: []
};

// MAnual resets (debug)
if (msg.payload === "reset") {
    flow.set('target_engine_buffer', undefined);
    node.status({fill:"red", shape:"dot", text:"Buffer Resettato"});
    return null;
}

// Sensor Type Extraction
var sensorType = msg.topic.split("/").pop();

// Sensor value
var val = msg.payload.value !== undefined ? msg.payload.value : msg.payload;

// Conversion from g to mm/s2
if (sensorType === "vibration") {
    var acc_mms2 = val * G_TO_MMS2;
    val = acc_mms2 / OMEGA;

    // 2 decimals
    val = Math.round(val * 100) / 100;
}

buffer.currentReading[sensorType] = val;

var cur = buffer.currentReading;

if (cur.vibration !== undefined && cur.temperature !== undefined && cur.current !== undefined) {

    // Pydantic object 
    var readingObj = {
        vibration: cur.vibration,
        temperature: cur.temperature,
        current: cur.current
    };

    buffer.history.push(readingObj);

    // Recupera le chiavi che abbiamo attualmente nel "limbo"
    var keysPresent = Object.keys(buffer.currentReading).join(", ");

    // Status colorato: 
    // Giallo = In attesa di dati per completare la tripletta
    // Blu = Tripletta completata e aggiunta alla storia
    node.status({
        fill: "yellow", 
        shape: "ring", 
        text: "Buf: " + buffer.history.length + "/50 | Ho: [" + keysPresent + "]"
    });

    buffer.currentReading = {};

    // Take only last 50 elements
    if (buffer.history.length > SEQUENCE_LENGTH) {
        buffer.history.shift();
    }

    // Trigger
    // if 50 elements -> send to the model
    if (buffer.history.length === SEQUENCE_LENGTH) {
        // Save updated state
        flow.set('target_engine_buffer', buffer)

        // FastAPI Payload
        var apiPayload = {
            asset_id: TARGET_ASSET_ID,
            sequence: buffer.history
        }

        // Set URL and Payload for HTTP Request
        msg.url = AI_SERVICE_URL;
        msg.payload = apiPayload;

        // Metadata for next nodes
        msg.assetId = TARGET_ASSET_ID;

        return msg;
    }
}
// Save updated state
flow.set('target_engine_buffer', buffer);

// Stop flow, not ready
return null;

const RUL_THRESHOLD = 400;

// Server Response structure: { "asset_id": "...", "predicted_rul": 123.45, "timestamp": "...", "status": "OK" }
var payload = msg.payload;

var rul = payload.predicted_rul;
var timestamp = payload.timestamp;
var assetId = payload.asset_id;

// Asset Id parsing
var parts = assetId.match(/(sector_\d+)_(line_\d+)_(engine_\d+)/);

var sectorTag = parts ? parts[1] : "unknown"; // es. sector_1
var lineTag   = parts ? parts[2] : "unknown"; // es. line_1
var engineTag = parts ? parts[3] : "unknown"; // es. engine_1

// Influxdb formatting
var dbMessage = {

    timestamp: new Date(timestamp),

    payload: 
    [
        {
            value: rul,
            unit: "cycles",
            warning_threshold: RUL_THRESHOLD
        },

        {
            sector: sectorTag,
            line: lineTag,
            engine: engineTag,
            sensor_type: "ai_rul",
            source: "rnn_model"
        }
    ]

}

// Alerting formatting
var alertMessageObj = null;

if (rul < RUL_THRESHOLD) {
    var message = `‚ö†Ô∏è *PREDICTIVE MAINTENANCE REQUIRED*\n\n` +
               `üè≠ Engine: ${assetId}\n` +
               `üìâ Predicted RUL: *${rul} cicli*\n` +
               `‚è∞ Time: ${timestamp}\n` +
               `\nThe engine has exceeded the safety threshold (${RUL_THRESHOLD}). ` +
               `Plan immediate intervention.`;

    alertMessageObj = {
        payload: {
            engine: engineTag,
            value: rul,
            warning_threshold: RUL_THRESHOLD,
            message: message,
            timestamp: timestamp
        }
    };
}

return [dbMessage, alertMessageObj];
