  |> filter(fn: (r) => r["_field"] == "value" or r["_field"] == "warning_limit" or r["_field"] == "critical_limit")

  // 3. Rinominiamo i campi per renderli leggibili nella legenda
  |> map(fn: (r) => ({
      r with 
      _field: 
        if r._field == "value" then "Temperatura (°C)" 
        else if r._field == "warning_limit" then "Warning" 
        else "Critical"
    })
