import "strings"

from(bucket: "iot_bucket")
  |> range(start: v.timeRangeStart, stop: v.timeRangeStop)
  |> filter(fn: (r) => r["_measurement"] == "machine_telemetry")
  |> filter(fn: (r) => r["sector"] =~ /${sector:regex}/)
  |> filter(fn: (r) => r["line"]   =~ /${line:regex}/)
  |> filter(fn: (r) => r["engine"]  =~ /${engine:regex}/)
  |> filter(fn: (r) => r["sensor_type"] == "temperature")
  |> filter(fn: (r) => r["_field"] == "value")
  
  // --- LOGICA INTELLIGENTE --
  |> map(fn: (r) => {
      // 1. Prepariamo le versioni corte
      short_s = "S" + strings.replaceAll(v: r.sector, t: "sector_", u: "")
      short_l = "L" + strings.replaceAll(v: r.line, t: "line_", u: "")
      short_e = "E" + strings.replaceAll(v: r.engine, t: "engine_", u: "")
      
      // 2. Componiamo le due stringhe complete
      label_short = short_s + "/" + short_l + "/" + short_e
      label_long  = r.sector + "/" + r.line + "/" + r.engine
      
      // 3. IF/ELSE: Se la variabile Grafana è ".*" (ALL), usa la corta, altrimenti la lunga
      // Nota: usiamo "${sector}" con le virgolette per trattarlo come stringa
      final_label = if "${sector}" == ".*" then label_short else label_long

      // Restituiamo la riga aggiornando il campo "_field" con la nostra etichetta dinamica
      return { r with _field: final_label }
  })

  |> last()
  |> keep(columns: ["_value", "_time", "_field"])
  |> yield(name: "Temp")

import "strings"

from(bucket: "iot_bucket")
  |> range(start: v.timeRangeStart, stop: v.timeRangeStop)
  |> filter(fn: (r) => r["_measurement"] == "machine_telemetry")
  |> filter(fn: (r) => r["sector"] =~ /${sector:regex}/)
  |> filter(fn: (r) => r["line"]   =~ /${line:regex}/)
  |> filter(fn: (r) => r["engine"]  =~ /${engine:regex}/)
  // FILTRO FONDAMENTALE: Cerchiamo solo la corrente
  |> filter(fn: (r) => r["sensor_type"] == "current")
  |> filter(fn: (r) => r["_field"] == "value")

  // --- INIZIO LOGICA ETICHETTE INTELLIGENTI ---
  |> map(fn: (r) => {
      // Creiamo le versioni corte
      short_s = "S" + strings.replaceAll(v: r.sector, t: "sector_", u: "")
      short_l = "L" + strings.replaceAll(v: r.line, t: "line_", u: "")
      short_e = "E" + strings.replaceAll(v: r.engine, t: "engine_", u: "")

      // Componiamo le due opzioni
      label_short = short_s + "/" + short_l + "/" + short_e
      label_long  = r.sector + "/" + r.line + "/" + r.engine

      // Selettore: Se "sector" è ALL (.*), usa la corta. Altrimenti la lunga.
      final_label = if "${sector}" == ".*" then label_short else label_long

      // Sostituiamo il nome del field con la nostra etichetta
      return { r with _field: final_label }
  })
  // ------------------------------------------

  |> last()
  // Pulizia finale per evitare che Grafana mostri tag residui
  |> keep(columns: ["_value", "_time", "_field"]) 
  |> yield(name: "Current")

// 1. Definiamo i Target
target_sector = /^${sector}$/
target_line   = /^${line}$/
target_engine = /^${engine}$/

// 2. Scarichiamo TUTTI i dati (Valore + Limiti)
from(bucket: "iot_bucket")
  |> range(start: v.timeRangeStart, stop: v.timeRangeStop)
  |> filter(fn: (r) => r["_measurement"] == "machine_telemetry")
  |> filter(fn: (r) => r["sector"] =~ target_sector)
  |> filter(fn: (r) => r["line"]   =~ target_line)
  |> filter(fn: (r) => r["engine"] =~ target_engine)
  |> filter(fn: (r) => r["sensor_type"] == "vibration")
  // Prendiamo valore grezzo E limiti grezzi
  |> filter(fn: (r) => r["_field"] == "value" or r["_field"] == "warning_limit" or r["_field"] == "critical_limit")
  // Pulizia tag inutili
  |> keep(columns: ["_time", "_value", "_field", "engine", "line", "sector"])

  // 3. CONVERSIONE E RINOMINA (Tutto x 31.21)
  
  |> map(fn: (r) => ({ r with _value: r._value * 31.21 })) 
  |> map(fn: (r) => ({
      r with 
      _field: 
        if r._field == "value" then "Velocità ISO (mm/s)" 
        else if r._field == "warning_limit" then "Warning" 
        else "Critical"
    })
  )
  |> yield(name: "velocity_only")

import "strings"

from(bucket: "iot_bucket")
  |> range(start: v.timeRangeStart, stop: v.timeRangeStop)
  |> filter(fn: (r) => r["_measurement"] == "machine_telemetry")
  |> filter(fn: (r) => r["sector"] =~ /${sector:regex}/)
  |> filter(fn: (r) => r["line"]   =~ /${line:regex}/)
  |> filter(fn: (r) => r["engine"]  =~ /${engine:regex}/)
  |> filter(fn: (r) => r["sensor_type"] == "vibration")
  |> filter(fn: (r) => r["_field"] == "value" or r["_field"] == "warning_limit" or r["_field"] == "critical_limit")
  
  // 1. CONVERSIONE (g -> mm/s)
  |> map(fn: (r) => ({ r with _value: r._value * 31.21 }))

  // 2. Pivot
  |> pivot(rowKey:["_time"], columnKey: ["_field"], valueColumn: "_value")

  // 3. Etichette Intelligenti
  |> map(fn: (r) => {
      short_s = "S" + strings.replaceAll(v: r.sector, t: "sector_", u: "")
      short_l = "L" + strings.replaceAll(v: r.line, t: "line_", u: "")
      short_e = "E" + strings.replaceAll(v: r.engine, t: "engine_", u: "")
      
      label_short = short_s + "/" + short_l + "/" + short_e
      label_long  = r.sector + "/" + r.line + "/" + r.engine
      
      final_label = if "${sector}" == ".*" then label_short else label_long

      // Restituiamo il valore convertito
      return { r with _field: final_label, _value: r.value }
  })
  
  |> last()
  |> keep(columns: ["_value", "_time", "_field"])
  
  // --- MODIFICA FONDAMENTALE QUI SOTTO ---
  // Raggruppiamo per _field. Questo dice a Grafana: "Ogni nome diverso è un Gauge diverso"
  |> group(columns: ["_field"]) 
  
  |> yield(name: "Vibration_mm_s")
